header {
  background: #fcfcfc;
  font-family: "bahnschrift", Georgia;
  width: 100%;
  height: 60px;
  margin: 0 0 100px 0; }
  header a {
    text-decoration: none;
    font-style: none;
    color: #827C77; }
  header ul {
    display: flex;
    justify-content: flex-end;
    margin: 0 55px 20px 0; }
  header li {
    margin: 20px 0 0 50px;
    letter-spacing: 1px; }
  header a::after {
    content: "";
    display: block;
    width: 0;
    background: #3F3C39;
    height: 3px;
    transition: all 0.4s;
    transform: translateY(5px);
    color: #3F3C39;
    font-weight: bold; }
  header a:hover::after {
    width: 100%; }
  header a:hover {
    color: #3F3C39;
    font-weight: bold;
    transition: all 0.1 0.5s; }

